package Week1Day1;



public class Calculator {

	
		// TODO Auto-generated constructor stub
		
		public int add(int A, int B, int C)
		{
			return(A+B+C);
			
		}
		public int sub(int A1, int B1)
		{
			return(A1-B1);
			
		}
		public int div(int A2, int B2)
		{
			return(A2/B2);
			
		}
		public int mul(int A3, int B3)
		{
			return(A3*B3);
			
		
	}


}